#include <stdio.h>
#include <string.h>
#include <math.h>
#include <stdlib.h>
int fact(int x)
{
	int i;long long int fac=1;
	for(i=2;i<=x;i++)
	fac=fac*i;
	return fac;
}
int main()
{
    int n,t,i,j,chk,p=0;
    scanf("%d%d",&n,&t);
    int a[n];int b[(fact(n))/(fact(2)*fact(n-2))];
    for(i=0;i<(fact(n))/(fact(2)*fact(n-2));i++)
        b[i]=t;
    for(i=0;i<n;i++)
        scanf("%d",&a[i]);
    for(i=0;i<n;i++)
    {
        for(j=i+1;j<n;j++)
        {
            chk=a[i]+a[j];
            while(chk>0)
            {
                if(chk % 10==0)
                    b[p]=b[p]-1;
                chk=chk/10;
            }
            ++p;
        }
    }
    int largest=b[0];
    for(i=0;i<(fact(n))/(fact(2)*fact(n-2));i++)
    {
        if (largest < b[i])
            largest = b[i];
    }
    int count=0;
    for(i=0;i<(fact(n))/(fact(2)*fact(n-2));i++)
    {
        if (largest == b[i])
            ++count;
    }
    printf("%d\n%d",largest,count);
    return 0;
}

