		for(j=i+1;j<n;j++)
		b[k++] = a[i]+a[j];

		sort(&b[0],&b[k]);

		ll ans  = 0;
		for(i=0;i<k;i++)
		{
			//cout<<b[i]<<" ";
			ans += (i - (k-i-1))*2*(ll)b[i]; //k instead of n
		}
		//cout<<ans<<"\n";
		for(i=0;i<n;i++)
		for(j=i+1;j<n;j++)
		{
			//cout<<a[j]-a[i]<<" ";
			ans -= 2*(n-2)*(ll)(a[j]-a[i]);
		}
		printf("%lld\n",ans);

	}
	return 0;
}

