int minuteToWinIt(vector<int> a, int k) {
    // Return the minimum amount of time in minutes.
    int n=a.size();
    vector<int> b(a);
    int minutes1=0,minutes2=0;
    for(int i=1;i<n;i++)
    {
        if(a[i]-a[i-1]!=k)
        {
            a[i]=a[i-1]+k;
            ++minutes1;
        }
        if(b[n-i]-b[n-i-1]!=k)
        {
            b[n-i-1]=b[n-i]-k;
            ++minutes2;
        }
    }
    return (minutes1<minutes2)?minutes1:minutes2;
}

