    mp[p2].clear();
    par[p2]=p1;
}
inline void dfs(int pos,int prev)
{
    subsize[pos]=a[pos];
    for(int i=0;i<g[pos].size();i++)
    {
        if(g[pos][i]!=prev)
        {
            dfs(g[pos][i],pos);
            subsize[pos]+=subsize[g[pos][i]];
        }
    }
}
inline void dfs1(int pos,int prev)
{
    for(int i=0;i<g[pos].size();i++)
    {
        if(g[pos][i]!=prev)
        {
            dfs1(g[pos][i],pos);
            merge(pos,g[pos][i]);
        }
    }
    int p1=parent(pos);
    mp[p1][subsize[pos]]++;
    if(pos==1)
    {
        return;
    }
    if(subsize[pos]%2==0)
    {
        if(tot-subsize[pos]<=(subsize[pos]/2)&&mp[p1][subsize[pos]/2]>0)
        {
            // cout<<pos<<" 1\n";
            ans=min(ans,(subsize[pos]/2)-(tot-subsize[pos]));
        }
    }
    if(subsize[pos]>=tot-subsize[pos])
    {
        if(mp[p1][tot-subsize[pos]]>0||(mp[p1][2*subsize[pos]-tot]>0))
        {
            if(subsize[pos]-(tot-subsize[pos])<=tot-subsize[pos])
            {
                // cout<<pos<<" 2\n";
                ans=min(ans,(tot-subsize[pos])-(subsize[pos]-(tot-subsize[pos])));
            }
        }
    }
    if(mp1[subsize[pos]]>=2)
    {
        if(tot-(2*subsize[pos])<=subsize[pos])
        {
            // cout<<pos<<" 3\n";
            ans=min(ans,subsize[pos]-(tot-(2*subsize[pos])));
        }
    }
    // cout<<pos<<" "<<subsize[pos]<<" "<<mp[p1][tot-2*subsize[pos]]<<"\n";
    if((tot-2*subsize[pos])<=subsize[pos])
    {
        if(mp1[tot-2*subsize[pos]]-mp[p1][tot-2*subsize[pos]]>0)
        {
            // cout<<pos<<" 4\n";
            // cout<<subsize[pos]<<"\n";
            ans=min(ans,subsize[pos]-(tot-2*subsize[pos]));
        }
    }
}
int main()
{
    int t;
    sd(t);
    while(t--)
    {
        int n;
        sd(n);
        ans=100000000000000000;
        for(int i=1;i<=n;i++)
        {
            sdl(a[i]);
        }
        for(int i=0;i<n-1;i++)
        {
            int x,y;
            sd(x);
            sd(y);
            g[x].pb(y);
            g[y].pb(x);
        }
        for(int i=0;i<=n;i++)
        {
            par[i]=i;
        }
        dfs(1,0);
        tot=subsize[1];
        for(int i=1;i<=n;i++)
        {
            mp1[subsize[i]]++;
        }
        dfs1(1,0);
        for(int i=1;i<=n;i++)
        {
            g[i].clear();
            mp[i].clear();
        }
        mp1.clear();
        if(ans==100000000000000000)
        {
            printf("-1\n");
            continue;
        }
        pdl(ans);
    }
}

