// Complete the caesarCipher function below.
string caesarCipher(string s, int k) {
    k=k%26;
    for(int i=0;i<s.length();i++) {
        if(s[i] >= 'a' && s[i] <= 'z') {
            s[i]+=k;
            if(s[i] >= 'a' && s[i] <= 'z');
            else
                s[i]-=26;
        }
        else if(s[i] >= 'A' && s[i] <= 'Z') {
            s[i]+=k;
            if(s[i] >= 'A' && s[i] <= 'Z');
            else
                s[i]-=26;
        }
    }
    return s;
}

