// Complete the mandragora function below.
long mandragora(vector<ll> h) {
    ll n=h.size();
    sort(h.rbegin(),h.rend());
    ll e,max=h[0]*n;
    for(ll i=1;i<n;i++) {
        h[i]+=h[i-1];
        e=h[i]*(n-i);
        if(e>max)
            max=e;
    }
    return max;
}

