int luckBalance(int n, int k, vector<int> contests1, vector<int> contests0) {
    // Complete this function
    int i,sum=0;
    sort(contests1.begin(), contests1.begin() + contests1.size()-1);
    for(i=0;i<contests0.size();i++)
        sum=sum+contests0[i];
    i=contests1.size()-1;
    while(k-- && i--)
        sum=sum+contests1[i];
    while(i--)
        sum=sum-contests1[i];
    return sum;
    }

int main() {
    int n,k,temp0,temp1;
    cin >> n >> k;
    vector<int> contests1(n);
    vector<int> contests0(n);
    for(int i = 0;i < n;i++) {
        cin >> temp0;
        cin >> temp1;
        if(temp1==1)
            contests1[i]=temp0;
        else
            contests0[i]=temp0;
    }
    int result = luckBalance(n, k, contests1, contests0);
    cout << result << endl;
    return 0;
}

