 */
int minimum(vector<int> b)
{
    int x = b[0];
    for(int i=1;i<b.size();i++)
        if(x>b[i])
            x=b[i];
    return x;
}
int getTotalX(vector<int> a, vector<int> b)
{
    vector<int> factors;
    int minb=minimum(b);
    for(int i=1;i<=sqrt(minb);i++)
    {
        if(minb%i==0)
        {
            factors.push_back(i);
            if(i!=minb/i)
            factors.push_back(minb/i);
        }
    }
    for(int i=0;i<b.size();i++)
    {
       for(int j=0;j<factors.size();j++)
       {
           if(!(b[i]%factors[j]==0))
           {
               factors.erase(factors.begin()+j);
               j--;
           }
       }
    }
    for(int i=0;i<a.size();i++)
    {
       for(int j=0;j<factors.size();j++)
       {
           if(!(factors[j]%a[i]==0))
           {
               factors.erase(factors.begin()+j);
               j--;
           }
       }
    }
    return factors.size();
}

