// Complete the workbook function below.
int workbook(int n, int k, vector<int> arr)
{
    int count=0,a,b,page=1;
    for(int i=0;i<n;i++)
    {
        a=1-k;b=0;
        for(int j=1;j<=arr[i]/k;j++)
        {
            a=a+k;
            b=b+k;
            if(page>=a && page<=b)
                ++count;
            ++page;
        }
        if(arr[i] % k != 0)
        {
            a=a+k;
            b=b+(arr[i] % k);
            if(page>=a && page<=b)
                ++count;
            ++page;
        }
    }
    return count;
}

