// Complete the flatlandSpaceStations function below.
int flatlandSpaceStations(int n, vector<int> c)
{
    int max=0;
    int n_stations=c.size();
    sort(c.begin(),c.end());
    for(int i=0;i<n_stations-1;i++)
        if(((c[i+1]-c[i])/2)>max)
            max=((c[i+1]-c[i])/2);
    if(c[0]>max)
        max=c[0];
    if(((n-1)-c[n_stations-1])>max)
        max=((n-1)-c[n_stations-1]);
    return max;
}

