void fairRations(vector<int> b)
{
    int count=0,i;
    for(i=0;i<b.size()-1;i++)
    {
        if(b[i]%2==0)
            continue;
        b[i]+=1;
        b[i+1]+=1;
        count+=2;
    }
    if(b[i]%2==0)
        cout<<count;
    else
        cout<<"NO";
}

