// Complete the hourglassSum function below.
int hourglassSum(vector<vector<int>> arr) 
{
    int i,j,sum,max=-1000;
    for(i=0;i<4;i++)
    {
        for(j=1;j<5;j++)
        {
            sum=arr[i][j-1]+arr[i][j]+arr[i][j+1]
                           +arr[i+1][j]
                +arr[i+2][j-1]+arr[i+2][j]+arr[i+2][j+1];
            if(sum>max)
                max=sum;
        }
    }
    return max;
}

