// Complete the cutTheSticks function below.
vector<int> cutTheSticks(vector<int> arr)
{
    int n=arr.size(),i=0;
    vector<int> sc;
    sort(arr.begin(),arr.end());
    while(arr[n-1])
    {
        while(arr[i]==0)
            ++i;
        sc.push_back(n-i);
        int temp=arr[i];
        for(int j=0;j<n;j++)
            if(arr[j]!=0)
                arr[j]-=temp;
    }
    return sc;
}

