// Complete the repeatedString function below.
long repeatedString(string s, long n) {
    long count_a=0,n_strings,part_s;
    for(int i=0;i<s.length();i++)
        if(s[i]=='a')
            ++count_a;
    n_strings=n/s.length();
    part_s=n%s.length();
    count_a*=n_strings;
    for(int i=0;i<part_s;i++)
        if(s[i]=='a')
            ++count_a;
    return count_a;
}

