string encryption(string s) {
    s.erase(remove(s.begin(),s.end(),' '),s.end());
    int row=floor(sqrt(s.length())),col=ceil(sqrt(s.length()));
    if(row*col<s.length());
        row+=1;
    char c[col][row+1];
    // cout<<row<<" "<<col<<endl;
    // for(int i=0;i<col;i++)
