int equal_helper(vector<int> a,int min) {
  int n = a.size(), steps = 0;
  for (int i = 0; i < n; i++) {
    int diff = a[i] - min;
    int m = diff / 5;
    diff = diff % 5;
    steps += m;
    if (diff == 1 || diff == 2)
      steps += 1;
    else if (diff == 3 || diff == 4)
      steps += 2;
    a[i] = min;
  }
  return steps;
}
