// Complete the isBalanced function below.
string isBalanced(string s) {
  stack<char> c;
  for (int i = 0; i < s.length(); i++) {
    if (s[i] == '{' || s[i] == '(' || s[i] == '[')
      c.push(s[i]);
    else if (!c.empty() && s[i] - c.top() < 3)
      c.pop();
    else
      return "NO";
  }
  if (c.empty())
    return "YES";
  else
    return "NO";
}

