int unboundedKnapsack(int k, vector<int> arr) {
    int max = 0;
    for(int i=0;i<arr.size();i++) {
        if(arr[i]<=k) {
            int x = arr[i] + unboundedKnapsack(k-arr[i],arr);
            if(x > max) max=x;
