                if(arr[j]+dp[i-arr[j]] > dp[i])
                    dp[i]=arr[j]+dp[i-arr[j]];
    return dp[k];
}
int main() {
    ofstream fout(getenv("OUTPUT_PATH"));
    int t,n,k;
    cin >> t;
    while(t--) {
        memset(dp,0,sizeof(dp));
        cin>>n>>k;
        vector<int> arr(n);
        for (int i = 0; i < n; i++) cin>>arr[i];
        int result = unboundedKnapsack(k, arr);
        fout << result << "\n";
    }
    fout.close();
    return 0;
}

