// Complete the stones function below.
vector<int> stones(int n, int a, int b) {
    vector<int> stone;
    n=n-1;
    if(b==a) {
        stone.push_back(n*a);
        return stone;
    }
    if(b<a) swap(a,b);
    for(int i=0;i<=n;i++)
        stone.push_back(((n-i)*a)+(i*b));
    return stone;
}

