int chocolateFeast(int n, int c, int m) {
    int chocolates=n/c;
    int choco_offer=0;
    int wrapers=chocolates;
    while(wrapers>=m) {
        choco_offer+=(wrapers/m);
        wrapers%=m;
        wrapers+=choco_offer;
        chocolates+=choco_offer;
        choco_offer=0;
    } return chocolates;
}
