// Complete the biggerIsGreater function below.
bool biggerIsGreater(string &w,int i=0) {
    if(w.length()==i-1)
        return false;
    if(biggerIsGreater(w,i+1))
        return true;
    else {
        for(int j=w.length()-1;j>i;j--) {
            if(w[j]>w[i]) {
                swap(w[j],w[i]);
                sort(w.begin()+i+1,w.end());
                return true;
            }
        }
    }
    return false;
}
