class Box {
    int l,b,h;
    public:
    Box() {
        l=0; b=0; h=0;
    }
    Box(int L,int B,int H) {
        l=L; b=B; h=H;
    }
    Box(Box &a) {
        l=a.getLength(); b=a.getBreadth(); h=a.getHeight();
    }
    int getLength() {
        return l;
    }
    int getBreadth () {
        return b;
    }
    int getHeight () {
        return h;
    }
    long long CalculateVolume() {
        return l*b*h;
    }
    bool operator<(Box& a) {
