// Complete the absolutePermutation function below.
vector<int> absolutePermutation(int n, int k) {
    vector<int> absPerm(n);
    vector<bool> used(n);
    for(int i=0;i<n;i++) {
        if(i-k>=0 && !used[i-k]) {
            absPerm[i]=i-k+1;
            used[i-k]=true;
        } else if(i+k<n && !used[i+k]) {
            absPerm[i]=i+k+1;
            used[i+k]=true;
        } else {
            vector<int> NoAns;
            NoAns.push_back(-1);
            return NoAns;
        }
    } return absPerm;
}
