int isPalindrome(string s) {
    int n=s.length();
    for(int i=0;i<n/2;i++)
        if(s[i]!=s[n-i-1])
            return i;
    return -1;
}
int palindromeIndex(string s1) {
    string s2=s1;
    int n=s1.length();
    int ind=isPalindrome(s1);
    if(ind<0) return -1;
    s1.erase(ind,1);
    s2.erase(n-1-ind,1);
    int ind1=isPalindrome(s1);
    int ind2=isPalindrome(s2);
    if(ind1<0) return ind;
    else if(ind2<0) return n-1-ind;
    else return -1;
}

