            v_end=ob[i][0];
        if(abs(r_q-ob[i][0])==abs(c_q-ob[i][1])) {
            if(ob[i][0]<r_q && ob[i][1]<c_q && d1_start<ob[i][0])
                d1_start=ob[i][0];
            if(ob[i][0]>r_q && ob[i][1]>c_q && d1_end>ob[i][0])
